tinymce.PluginManager.add('internalimage', function (editor, url) {
	editor.addButton('internalimage', {
		tooltip: 'Insert Image',
		image: url + '/icon.png',
		onclick: function () {
			var el = editor.dom.create('div', { 'class': 'mf-image-block mceNonEditable' }, '<img src="http://res.mefair.org/Slides/slide1.en.jpg" alt="" />');
			InsertElementAtRoot(editor, el);
		}
	});
});